/* Modal Styles */
.max-h-90vh {
    max-height: 90vh;
}

#profileModal .bg-white {
    max-width: 90vw;
    max-height: 90vh;
}

@media (max-width: 768px) {
    #profileModal .bg-white {
        margin: 1rem;
        max-width: calc(100vw - 2rem);
    }
}

/* Profile Modal Animation */
#profileModal {
    animation: fadeIn 0.3s ease-out;
}

#profileModal .bg-white {
    animation: slideIn 0.3s ease-out;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes slideIn {
    from { 
        transform: translateY(-50px);
        opacity: 0;
    }
    to { 
        transform: translateY(0);
        opacity: 1;
    }
}

